ALTER PROCEDURE Projectuser.Pr_RequisitionAttachments_CUD
(	
	@actionType						TINYINT,	--(Notes: 1 = Insert, 2 = Update, 3 = Delete)	
	@requisitionAttachmentID		INT OUTPUT, 
	@requisitionID					INT,
	@attachmentFileName				VARCHAR(255) = NULL,
	@attachmentDisplayName			VARCHAR(255) = NULL,
	@attachmentSize					INT = NULL,
	@userEmpNo						INT = NULL,
	@userID							VARCHAR(50) = NULL,
	@base64File						VARCHAR(MAX) = NULL,
	@base64FileExt					VARCHAR(5) = NULL
)
AS	
BEGIN

	--Define constants
	DECLARE @CONST_RETURN_OK	INT = 0,
			@CONST_RETURN_ERROR	INT = -1

	--Define variables
	DECLARE @rowsAffected		INT = 0,
			@hasError			BIT = 0,
			@retError			INT = @CONST_RETURN_OK,
			@retErrorDesc		VARCHAR(200) = '',
			@return_value		INT = 0,
			@retErrorMessage	VARCHAR(1000) = ''

	IF @actionType = 1		--Insert record
	BEGIN

		INSERT INTO dbo.RequisitionAttachments
		(
			RequisitionID,
			AttachmentFileName,
			AttachmentDisplayName,
			AttachmentSize,
			CreatedByEmpNo,
			CreatedBy,
			CreatedDate,
			LasUpdatedBy,
			LastUpdatedDate,
			Base64File,
			Base64FileExt
		)
		SELECT	CAST(@requisitionID AS NUMERIC(18,0)), 
				@attachmentFileName, 
				@attachmentDisplayName,
				CAST(@attachmentSize AS NUMERIC(18,0)), 
				@userEmpNo, 
				@userID, 
				GETDATE(),
				@userID, 
				GETDATE(),
				@base64File,
				@base64FileExt

		--Get the new ID
		SELECT @requisitionAttachmentID = CAST(@@IDENTITY AS INT)

		--Checks for error
		IF @@ERROR <> @CONST_RETURN_OK
		BEGIN
				
			SELECT	@hasError = 1,
					@retError = CASE WHEN ERROR_NUMBER() > 0 THEN ERROR_NUMBER() ELSE @CONST_RETURN_ERROR END,
					@retErrorDesc = ERROR_MESSAGE()
		END

		--Return error information to the caller
		SELECT	@requisitionAttachmentID AS NewIdentityID,
				@rowsAffected AS RowsAffected,
				@hasError AS HasError, 
				@retError AS ErrorCode, 
				@retErrorDesc AS ErrorDescription
	END 

	ELSE IF @actionType = 2		--Update existing record
	BEGIN

		UPDATE dbo.RequisitionAttachments
		SET AttachmentFileName = @attachmentFileName,
			AttachmentDisplayName = @attachmentDisplayName,
			AttachmentSize = @attachmentSize,
			Base64File = @base64File,
			@base64FileExt = @base64FileExt,
			LasUpdatedBy = @userID,
			LastUpdatedDate = GETDATE()
		WHERE RequisitionAttachmentID = @requisitionAttachmentID

		SELECT @rowsAffected = @@rowcount 

		--Checks for error
		IF @@ERROR <> @CONST_RETURN_OK
		BEGIN
				
			SELECT	@hasError = 1,
					@retError = CASE WHEN ERROR_NUMBER() > 0 THEN ERROR_NUMBER() ELSE @CONST_RETURN_ERROR END,
					@retErrorDesc = ERROR_MESSAGE()
		END

		--Return error information to the caller
		SELECT	@rowsAffected AS RowsAffected,
				@hasError AS HasError, 
				@retError AS ErrorCode, 
				@retErrorDesc AS ErrorDescription
	END 

	ELSE IF @actionType = 3		--Delete record
	BEGIN

		--Check existing records
		DELETE FROM dbo.RequisitionAttachments
		WHERE RequisitionID = @requisitionID

		--Get the number of affected records 
		SELECT @rowsAffected = @@rowcount 

		--Checks for error
		IF @@ERROR <> @CONST_RETURN_OK
		BEGIN
				
			SELECT	@hasError = 1,
					@retError = CASE WHEN ERROR_NUMBER() > 0 THEN ERROR_NUMBER() ELSE @CONST_RETURN_ERROR END,
					@retErrorDesc = ERROR_MESSAGE()
		END

		--Return error information to the caller
		SELECT	@rowsAffected AS RowsAffected,
				@hasError AS HasError, 
				@retError AS ErrorCode, 
				@retErrorDesc AS ErrorDescription
	END 

END 

